    clearTimeout(t1);
  }
}

const ScrollToTop = () => {
  return ( 
    <div className='scroll-top'>
    <p>your text here</p>
    <img id="toTop" src="http://via.placeholder.com/50x50" onclick="abcd()" title="Go To Top"></img>
    </div>
  )
}


export default ScrollToTop
